            <tr>
                <td>{{ match.id }}</td>
                <td>{{ match.score }}</td>
                <td>{{ match.fouls }}</td>
                <td>{{ match.penalty }}</td>
                <td>{{ match.yellowcard }}</td>
                <td>{{ match.redcard }}</td>
                <td>
                    <a href=\"{{ path('match_show', {'id': match.id}) }}\">show</a>
                    <a href=\"{{ path('match_edit', {'id': match.id}) }}\">edit</a>
